export const calcularTotal = (cantidad,plazo)=>{
    /*
    0-1000 = 25%;
    1001 - 5000 = 20%;
    5001 - 10000 = 15;
    +10000 = 10%
    */

    const totalCantidad = 0;

    if(cantidad > 0 && cantidad < 1000){
        totalCantidad = cantidad * 0.25; 
    }

}
